import React, { useState } from 'react';
import { Platform, View, Button, Alert } from 'react-native';
import DateTimePickerModal from 'react-native-modal-datetime-picker';

const AlarmClock = () => {
  const [selectedTime, setSelectedTime] = useState(null);
  const [showPicker, setShowPicker] = useState(false);

  const showTimePicker = () => {
    setShowPicker(true);
  };

  const handleTimeChange = (date) => {
    const formattedHour = date.getHours() < 10 ? `0${date.getHours()}` : date.getHours();
    const formattedMinute = date.getMinutes() < 10 ? `0${date.getMinutes()}` : date.getMinutes();
    const selectedTime = `${formattedHour}:${formattedMinute}`;
    setSelectedTime(selectedTime);
    setShowPicker(false);
    scheduleNotification(); // Schedule the notification here
  };

  const hideTimePicker = () => {
    setShowPicker(false);
  };

  const scheduleNotification = () => {
    if (selectedTime) {
      // Schedule the notification at the selected time
      // You can use a notification library or implement the logic based on your requirements
      // For example, you can use the react-native-push-notification library
      // Here's an example using the react-native-push-notification library:

      // import PushNotification from 'react-native-push-notification';
      // PushNotification.localNotificationSchedule({
      //   message: 'Alarm!',
      //   date: new Date(selectedTime),
      // });

      // Remember to install and configure the library according to its documentation
    }
  };

  return (
    <View>
      <Button title="Set Alarm" onPress={showTimePicker} />
      <DateTimePickerModal
        isVisible={showPicker}
        mode="time"
        onConfirm={handleTimeChange}
        onCancel={hideTimePicker}
      />
      {/* Rest of your code */}
    </View>
  );
};

export default AlarmClock;
