import React, { useState } from 'react';
import { Button, Text, View } from 'react-native';

const Character = (props) => {
  const [isPlay, setIsPlay] = useState(true);
  const [counter, setCounter] = useState(0);

  const play = () => {
    setIsPlay(false);
    setCounter(counter + 1);
  };

  const reset = () => {
    setIsPlay(true);
  };

  return (
    <View>
      <Text>
        I am {props.name}, and I {isPlay ? 'want to play with you' : 'want to have some rest'}!
        We've played {counter} times.
      </Text>
      {isPlay ? (
        <Button onPress={play} title="Play with me!" />
      ) : (
        <Button onPress={reset} title="Love you<3." />
      )}
    </View>
  );
};

const LivingRoom = () => {
  return (
    <>
      <Character name="Seele" />
      <Character name="Seele(black)" />
      <Character name="Bronya" />

    </>
  );
};

export default LivingRoom;
