import React, { useState } from 'react';
import { Platform, View, Button, Alert } from 'react-native';
import DateTimePickerModal from 'react-native-modal-datetime-picker';

const AlarmClock = () => {
  const [selectedTime, setSelectedTime] = useState(null);
  const [showPicker, setShowPicker] = useState(false);

  const showTimePicker = () => {
    setShowPicker(true);
  };

  const handleTimeChange = (date) => {
    const formattedHour = date.getHours() < 10 ? `0${date.getHours()}` : date.getHours();
    const formattedMinute = date.getMinutes() < 10 ? `0${date.getMinutes()}` : date.getMinutes();
    const selectedTime = `${formattedHour}:${formattedMinute}`;
    setSelectedTime(selectedTime);
    setShowPicker(false);
  };

  const hideTimePicker = () => {
    setShowPicker(false);
  };

  return (
    <View>
      <Button title="Set Alarm" onPress={showTimePicker} />
      <DateTimePickerModal
        isVisible={showPicker}
        mode="time"
        onConfirm={handleTimeChange}
        onCancel={hideTimePicker}
      />
      {/* Rest of your code */}
    </View>
  );
};

export default AlarmClock;
