import React, { useState } from 'react';
import { View, Button, Platform, FlatList, Text, Alert } from 'react-native';
import DateTimePicker from '@react-native-community/datetimepicker';
import PushNotification from 'react-native-push-notification';

const AlarmClock = () => {
  const [selectedTime, setSelectedTime] = useState(new Date());
  const [showTimePicker, setShowTimePicker] = useState(false);
  const [alarms, setAlarms] = useState([]);

  const handleTimeChange = (event, time) => {
    if (time) {
      setSelectedTime(time);
      setShowTimePicker(false);
      scheduleNotification(time);
    }
  };

  const scheduleNotification = (time) => {
    const newAlarm = { id: Date.now(), time };
    setAlarms([...alarms, newAlarm]);

    PushNotification.localNotification({
      message: 'Alarm!',
      subText: `Alarm set for ${time.toLocaleTimeString()}`,
      smallIcon: 'ic_notification',
    });

    console.log('Notification scheduled');
  };

  const deleteAlarm = (id) => {
    const updatedAlarms = alarms.filter((alarm) => alarm.id !== id);
    setAlarms(updatedAlarms);
  };

  const renderAlarm = ({ item }) => (
    <View style={{ flexDirection: 'row', alignItems: 'center', marginVertical: 10 }}>
      <Text style={{ flex: 1 }}>{item.time.toLocaleTimeString()}</Text>
      <Button title="Delete" onPress={() => deleteAlarm(item.id)} />
    </View>
  );

  const showPicker = () => {
    setShowTimePicker(true);
  };

  return (
    <View style={{ flex: 1 }}>
      <Button title="Set Alarm" onPress={showPicker} />
      {showTimePicker && (
        <DateTimePicker
          value={selectedTime}
          mode="time"
          display={Platform.OS === 'ios' ? 'spinner' : 'default'}
          onChange={handleTimeChange}
        />
      )}
      <FlatList
        data={alarms.sort((a, b) => a.time - b.time)}
        keyExtractor={(item) => item.id.toString()}
        renderItem={renderAlarm}
      />
    </View>
  );
};

export default AlarmClock;
