import React, { useState } from 'react';
import { StyleSheet, View, Text, TextInput, Button, Alert } from 'react-native';

const GuessNumber = () => {
  const [guess, setGuess] = useState('');
  const [attempts, setAttempts] = useState(0);
  const [answer] = useState(Math.floor(Math.random() * 100) + 1);

  const handleGuess = () => {
    const parsedGuess = parseInt(guess);
    if (isNaN(parsedGuess) || parsedGuess < 1 || parsedGuess > 100) {
      Alert.alert('Invalid guess', 'Please enter a number between 1 and 100.');
      return;
    }

    setAttempts(attempts + 1);

    if (parsedGuess === answer) {
      Alert.alert('Congratulations!', `You guessed the number in ${attempts} attempts.`);
      setGuess('');
      setAttempts(0);
      return;
    }

    if (parsedGuess < answer) {
      Alert.alert('Too Small', 'Try guessing a larger number.');
    } else {
      Alert.alert('Too Large', 'Try guessing a smaller number.');
    }
    setGuess('');
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Guess the Number</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter a number between 1 and 100"
        value={guess}
        onChangeText={setGuess}
        keyboardType="numeric"
      />
      <Button title="Guess" onPress={handleGuess} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 20,
    paddingHorizontal: 10,
  },
});

export default GuessNumber;
